import React from "react";
import Icon from "../icon";
import PaymentInput from "./paymentInput";

function Payment(){

  function onChange(){

  }

  return ( 
    <form className="" action="" method="post">
       <Icon 
          color = "#ff9500"
          typeIcon = "fas fa-pencil"
          toolTipText = "Ўзгартириш"
          classes = "payment-edit-div"
      />
      <div className="balance-div">
          <h3>Баланс: <span style={{fontSize: "20px"}}>0 Сўм</span></h3>
      </div>
      <div className="payment-details-div">
        <PaymentInput
          onChange= {onChange} 
          labelFor = "price"
          labelText = "Нарх:"
          inputType = "number"
          inputName = "price"
          inputValue = "300.000"
          isDisabled = {true}
          yesCheckBox = {false}
        />
         <PaymentInput
          onChange= {onChange} 
          labelFor = "cash"
          labelText = "Нақд:"
          inputType = "number"
          inputName = "cash"
          // inputValue = "300.000"
          isDisabled = {false}
          yesCheckBox = {true}
          checkBoxInputName = "cash-checkBox"
        />
        <PaymentInput
          onChange= {onChange} 
          labelFor = "card"
          labelText = "Карта:"
          inputType = "number"
          inputName = "card"
          // inputValue = "300.000"
          isDisabled = {false}
          yesCheckBox = {true}
          checkBoxInputName = "card-checkBox"
        />
        <PaymentInput
          onChange= {onChange} 
          labelFor = "debt"
          labelText = "Қарз:"
          inputType = "number"
          inputName = "debt"
          // inputValue = "300.000"
          isDisabled = {false}
          yesCheckBox = {true}
          checkBoxInputName = "debt-checkBox"
        />
        <PaymentInput
          onChange= {onChange} 
          labelFor = "debt_deadline"
          labelText = "Муддати:"
          inputType = "date"
          inputName = "debt_deadline"
          // inputValue = "300.000"
          isDisabled = {false}
          yesCheckBox = {false}
        />
      </div>

    </form>
  );
}

export default Payment;
