import React, { useEffect } from "react";
import Payment from "./payment";
import DataItem from "./dataItem";
import DeadlineCounter from "./deadLineCounter";

function Content(props) {
  useEffect(() => {
    let thisContent = document.querySelectorAll(".content")[props.id];

    if (props.isOpen) {
      thisContent.style.maxHeight = thisContent.scrollHeight + "px";
      thisContent.style.overflow = "visible";
    } else {
      thisContent.style.maxHeight = null;
      thisContent.style.overflow = "hidden";
    }
  });

  return (
    <div className="content">
      <div className="right-left-wrapping-div">
        <div className="left-side-content-div">
          <div className="content-row-div">
            <DataItem
              type="content-column-div"
              item1="Лицензия АТ №:"
              item2="AT 234567"
            />
            <DataItem
              type="content-column-div"
              item1="Лицензия муддати:"
              item2={
                <DeadlineCounter
                  deadLine="2022-2-21"
                  startDate="2022-01-20"
                  id={props.id}
                />
              }
            />
            <DataItem
              type="content-column-div"
              item1="Тиббий кўрик муддати:"
              item2={
                <DeadlineCounter
                  deadLine="2022-09-30"
                  startDate="2022-01-20"
                  id={props.id}
                />
              }
            />
          </div>

          <div className="content-row-div">
            <DataItem
              type="content-column-div"
              item1="Лицензия АТ №:"
              item2="AT 234567"
            />
            <DataItem
              type="content-column-div"
              item1="Лицензия муддати:"
              item2={
                <DeadlineCounter
                  deadLine="2022-2-21"
                  startDate="2022-01-20"
                  id={props.id}
                />
              }
            />
            <DataItem
              type="content-column-div"
              item1="Тиббий кўрик муддати:"
              item2={
                <DeadlineCounter
                  deadLine="2022-09-30"
                  startDate="2022-01-20"
                  id={props.id}
                />
              }
            />
          </div>
        </div>
        <div className="right-side-content-div">
          <Payment />
        </div>
      </div>
    </div>
  );
}

export default Content;
