import React, {useEffect, useState} from "react";
import Payment from "./payment";

function Content(props){
  let [height, setHeight] = useState(0);
  useEffect(()=>{
    let content = document.querySelectorAll(".content");
    let height = content[props.id].scrollHeight;
    setHeight(height);
  });

  const style = {
    maxHeight: props.isOpen? height + "px" : 0,
    overflow: props.isOpen? "visible" : "hidden"
  };

  return (
    <div className="content" style = {style}>
      <div className="right-left-wrapping-div">
      <div className="left-side-content-div">

      </div>
      <div className="right-side-content-div">
        <Payment />
      </div>
      </div>
    </div>
  )
}

export default Content;
