import React, { useEffect, useState } from "react";

function DeadlineCounter(props) {
  let [width, setWidth] = useState("100%");
  
  useEffect(()=>{
    const interval = setInterval(()=>{
      setWidth((prevV)=>{
        return (parseInt(prevV) - 1) + "%";
      })
    }, 1000);

  }, []);

  // useEffect(() => {
  //   const interval = setInterval(() => {
  //     setWidth((prevV)=>{
  //       return (parseInt(prevV) - 1) + "%";
  //     });
  //     // console.log('This will run every second!');
  //   }, 1000);
  //   // return () => clearInterval(interval);
  // }, []);
  
  function handleChange(e) {
    console.log(e.target.value);
  }

  return (
    <div>
      <input
        onChange={handleChange}
        className="input-disabled dead-line"
        type="date"
        value={new Date(props.deadLine).toISOString().slice(0, 10)}
        // disabled
      />
      <div className="progress-count"></div>
      <input
        type="hidden"
        value={new Date(props.startDate).toISOString().slice(0, 10)}
      />
      <div className="progress-div">
        <div className="progress-bar" style = {{width: width}}></div>
      </div>
    </div>
  );
}

export default DeadlineCounter;
