import React, { useState, useEffect } from "react";
import Header from "./header";
import Footer from "./footer";
import Wrapper from "./body/Wrapper";
import AddPersonPopUp from "./addPersonPopup/addPersonPopUp";
import BackPagePrint from "./print/backPage";

function App() {
  const [data, setData] = React.useState(null);

  // React.useEffect(() => {
  //   fetch("/a")
  //     .then((res) => res.json())
  //     .then((data) => setData(data.message));
  // }, []);

  const [popUpIsVisible, setPopUpIsVisible] = useState(false);
  function showPopUp() {
    setPopUpIsVisible(true);
  }

  const [backPagePrint, setBackPagePrint] = useState(false);
  function printBackPage() {
    setBackPagePrint(true);
    setTimeout(() => {
      setBackPagePrint(false);
    }, 100);
  }

  return (
    <div>
      <Header showPopUp={showPopUp} printBackPage={printBackPage} />
      <AddPersonPopUp
        popUpIsVisible={popUpIsVisible}
        setPopUpIsVisible={setPopUpIsVisible}
      />
      <BackPage backPagePrint={backPagePrint} />
      <Wrapper />
      <Footer />
    </div>
  );
}

export default App;
