import React, { useState, useRef, useEffect } from "react";
import Icon from "./icon";
import CalendarPrint from "./print/calendarPrint";
import BackPagePrint from "./print/backPage";


function useOutsideAlerter(ref) {
  useEffect(() => {
      /**
       * Alert if clicked on outside of element
       */
      function handleClickOutside(e) {
          if (ref.current && !ref.current.contains(e.target)) {
            console.log(ref);
            console.log(e.target);


              console.log("You clicked outside of me!");
          }
      }

      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
          // Unbind the event listener on clean up
          document.removeEventListener("mousedown", handleClickOutside);
      };
  }, [ref]);
}




function Header(props) {
  const [states, setStates] = useState({
    canPrintBackPage: false,
    calendarPrintVisible: false,
  });
  function clickBackPagePrintIcon() {
    setStates((prevStates) => {
      return {
        ...prevStates,
        calendarPrintVisible: true,
      };
    });
  }

  const calRef = useRef(null);
  useOutsideAlerter(calRef);

  

  return (
    <nav className="navigation-bar">
      <ul className="navigation-list">
        <li className="navigation-item">
          <h5>Буюк Ипак Йўли Транс</h5>
        </li>
        <li className="navigation-item">
          <Icon
            handleClick={props.showPopUp}
            typeIcon="fas fa-user-plus"
            toolTipText="Малумот Қўшиш"
            color="#06ab00"
          />
        </li>
        <li className="navigation-item" style={{position: "relative"}}>
          <Icon
            handleClick={clickBackPagePrintIcon}
            typeIcon="fas fa-print"
            toolTipText="Печатлаш"
            color="#2499ff"
          />
          <CalendarPrint calRef = {calRef} calendarPrintVisible={states.calendarPrintVisible}/>
          <BackPagePrint  />
        </li>

        <li className="navigation-item">
          <Icon
            typeIcon="fal fa-table"
            toolTipText="'Excel' да олиш"
            color="#ff9500"
          />
        </li>

        <li className="navigation-item">
          <Icon
            typeIcon="fas fa-analytics"
            toolTipText="Статистика"
            color="#ff9500"
          />
        </li>
        <li className="navigation-item">
          <Icon
            typeIcon="fas fa-sort"
            toolTipText="Филтерлаш"
            color="#ff9500"
          />
        </li>
        <li className="navigation-item">
          <Icon
            typeIcon="fas fa-trash"
            toolTipText="Ўчирилган Малумотлар"
            color="#e30505"
          />
        </li>
        <li className="navigation-item">
          <input
            type="text"
            placeholder="Излаш..."
            name="search"
            id="searchInput"
          />
        </li>
      </ul>
    </nav>
  );
}

export default Header;
