import React, { useState } from "react";
import "./calendarPrint.css";

function CalendarPrint() {
  const [year, setYear] = useState(new Date().getFullYear());

  function decrementYear() {
    setYear((prevV) => {
      return prevV --;
    });
  }

  function incrementYear() {
    setYear((prevV) => {
      return prevV ++;
    });
  }

  function chooseMonth(e) {
    let month = parseInt(e.target.id) + 1;
    let numberOfDays = new Date(year, month, 0).getDate();
    // console.log(numberOfDays);
    // console.log(month);

    for (let i = 1; i <= numberOfDays; i++) {
      const date = new Date(year, month - 1, i)
        .toLocaleDateString("en-UK")
        .replaceAll("/", ".");
      console.log(date);
    }
  }
  const months = [
    "Янв",
    "Фев",
    "Мар",
    "Апр",
    "Май",
    "Июн",
    "Июл",
    "Авг",
    "Сен",
    "Окт",
    "Ной",
    "Дек",
  ];
  console.log(new Date().getMonth());
  return (
    <div className="cal-container">
      <div className="cal-header">
        <div onClick={decrementYear}>{<i class="far fa-angle-left"></i>}</div>
        {year}
        <div onClick={incrementYear}>{<i class="far fa-angle-right"></i>}</div>
      </div>
      <div className="cal-body" onClick={chooseMonth}>
        {months.map((mon, index) => {
          return (
            <div
              id={index + "month"}
              style={
                index === new Date().getMonth()
                  ? { border: "1px solid lightseagreen" }
                  : null
              }
            >
              {mon}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default CalendarPrint;
