import React, {useState, useRef} from "react";
import Payment from "./payment";

function Content(props){
  // let [isOpen, setIsopen] = useState(props.isOpen);
  const [height, setHeight] = useState(0)
  const ref = useRef(null)

  useEffect(() => {
    setHeight(ref.current.clientHeight)
  })

  const style = {
    maxHeight: 0,
    overflow: "hidden"
  };
  const style2 = {
    maxHeight: 10 + "px",
    overflow: "visible"
  };


  return (
    <div className="content" style = {!props.isOpen ? style : style2}>
      <div className="right-left-wrapping-div">
      <div className="left-side-content-div">

      </div>
      <div className="right-side-content-div">
        <Payment />
      </div>
      </div>
    </div>
  )
}

export default Content;
