import React, { useEffect, useState } from "react";

function DeadlineCounter(props) {
  let [pWidth, setWidth] = useState(100);
  let deadLine = new Date(props.deadLine).getTime();
  let startDate = new Date(props.startDate).getTime();
  let today = new Date().getTime();

  let totalTime = deadLine - startDate;
  let timeLeft = deadLine - today;
  let secondsLeft = timeLeft/1000;

  useEffect(() => {
    const interval = setInterval(() => {
      secondsLeft--;
      let daysLeft = Math.floor(secondsLeft/(60*60*24));
      let hoursLeft = Math.floor((secondsLeft/(60*60))%24);
      let minutesLeft = Math.floor((secondsLeft/(60))%60);
      let progress = Math.floor((timeLeft/totalTime) * 100);
      let secondsL = Math.floor(secondsLeft%60);

    console.log(`Days: ${daysLeft} Hours: ${hoursLeft} Minutes: ${minutesLeft} Seconds: ${secondsL} Progress: ${progress}`);

      setWidth((prevV) => {
        return parseInt(prevV) - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  function handleChange(e) {
    console.log(e.target.value);
  }

  return (
    <div>
      <input
        onChange={handleChange}
        className="input-disabled dead-line"
        type="date"
        value={new Date(props.deadLine).toISOString().slice(0, 10)}
        // disabled
      />
      <div className="progress-count"></div>
      <input
        type="hidden"
        value={new Date(props.startDate).toISOString().slice(0, 10)}
      />
      <div className="progress-div">
        <div
          className="progress-bar"
          style={{
            width: pWidth + "%",
            backgroundColor: pWidth < 90 ? "red" : pWidth < 95 && "orange",
          }}
        ></div>
      </div>
    </div>
  );
}

export default DeadlineCounter;
