import React, { useEffect, useState } from "react";

function DeadlineCounter(props) {
  let [pWidth, setWidth] = useState(100);
  // let [color, setColor] = useState("");

  useEffect(() => {

    const interval = setInterval(() => {
      setWidth((prevV) => {
        return parseInt(prevV) - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, []);
  
  let color = "";
  if(pWidth<95){ color = ("orange")}else if(pWidth<90){color = ("red")}


 

  function handleChange(e) {
    console.log(e.target.value);
  }

  return (
    <div>
      <input
        onChange={handleChange}
        className="input-disabled dead-line"
        type="date"
        value={new Date(props.deadLine).toISOString().slice(0, 10)}
        // disabled
      />
      <div className="progress-count"></div>
      <input
        type="hidden"
        value={new Date(props.startDate).toISOString().slice(0, 10)}
      />
      <div className="progress-div">
        <div
          className="progress-bar"
          style={{ width: pWidth + "%", backgroundColor: color }}
        ></div>
      </div>
    </div>
  );
}

export default DeadlineCounter;
