import React, {useState} from "react";

function PaymentInput(props) {
  let [checked, setChecked] = useState(false);
  let [inputVal, setInputVal] = useState(null);

  function onChange(e){
    setInputVal(e.value)
  }
  function handleClick(e){
    setChecked((prevVal) => {
      return !prevVal;
    });
    console.log(e.target.checked)
  }
  return (
    <div className="payment-input-div">
      <label htmlFor={props.laberFor}>{props.labelText}</label>

      <input
        onChange={onChange}
        type={props.inputType}
        name={props.inputName}
        value={!props.inputValue && inputVal}
        // disabled={props.isDisabled && "disabled"}
        disabled={!checked && "disabled"}
      />
      {props.yesCheckBox ? (
        <input onClick = {handleClick} type="checkbox" name={props.checkBoxInputName} value="" />
      ) : null}
    </div>
  );
}

export default PaymentInput;
