import React, {useState} from "react";
import Collapsible from "./collapsible";
import Content from "./content";

function Card(props){
  let [isOpen, setIsOpen] = useState(false);

  function handleClick(id){
    console.log("clicked!" + isOpen + " id:" + id)
    setIsOpen((prevVal)=>{
      return !prevVal;
    });
  }

  
  return (
    <div className="one-person-div">
      <Collapsible  handleClick={handleClick}  id = {props.id} />
      <Content isOpen = {isOpen} id = {props.id}/>
    </div>
  );
}

export default Card;
