import React from "react";

function Icon(props){
 let style = {
               color: props.color, 
               cursor: "pointer",
            };
 let classes = [...props.classes, "my-tooltip"].join(" ");
 return  (
   <span className={"my-tooltip"} style = {style}>
      <i className ={props.typeIcon}></i>
      <span className="my-tooltiptext">{props.toolTipText}</span>
   </span>
);
};



export default Icon;
